import React, { useState } from 'react';

const HomePage = () => {
  const [activeTab, setActiveTab] = useState('intro');

  const handleTabClick = (tab) => {
    setActiveTab(tab);
  };

  return (
    <div className="homepage">
      <header>
        <h1>Welcome to Arthmate</h1>
      </header>
      <nav className="tabs">
        <button
          className={`tab ${activeTab === 'intro' ? 'active' : ''}`}
          onClick={() => handleTabClick('intro')}
        >
          Intro
        </button>
        <button
          className={`tab ${activeTab === 'team' ? 'active' : ''}`}
          onClick={() => handleTabClick('team')}
        >
          Team
        </button>
        <button
          className={`tab ${activeTab === 'what-we-do' ? 'active' : ''}`}
          onClick={() => handleTabClick('what-we-do')}
        >
          What We Do
        </button>
      </nav>
      <div className="content">
        {activeTab === 'intro' && (
          <div>
            <h2>About Arthmate</h2>
            <p>Insert your introductory content here.</p>
          </div>
        )}
        {activeTab === 'team' && (
          <div>
            <h2>Our Team</h2>
            <p>Insert information about your team members here.</p>
          </div>
        )}
        {activeTab === 'what-we-do' && (
          <div>
            <h2>What We Do</h2>
            <p>Insert information about what your company does here.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default HomePage;
