import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, NavLink } from 'react-router-dom';
import { Drawer, List, ListItem, ListItemIcon, ListItemText, AppBar, Toolbar, Button, Typography } from '@material-ui/core';
import { Home as HomeIcon, People as PeopleIcon, Info as InfoIcon } from '@material-ui/icons';
import './App.css'; // Import the CSS file for styling
import logo from './assets/logo.png'; // Import the logo image

import Introduction from './Introduction';
import Team from './Team';
import About from './About';

const HomePage = () => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  const sidebarItems = [
    { label: 'Home', icon: <HomeIcon />, to: '/' },
    { label: 'Team', icon: <PeopleIcon />, to: '/team' },
    { label: 'About', icon: <InfoIcon />, to: '/about' },
  ];

  return (
    <Router>
      <div className="app-container">
        <AppBar position="static">
          <Toolbar>
            <Button onClick={toggleSidebar} color="inherit">Open Sidebar</Button>
            <div className="logo-container">
              <img src={logo} alt="Logo" className="logo" />
              <Typography variant="h6" className="logo-text">My App</Typography>
            </div>
          </Toolbar>
        </AppBar>
        <Drawer open={isSidebarOpen} onClose={toggleSidebar}>
          <div className="sidebar">
            <List>
              {sidebarItems.map((item) => (
                <ListItem
                  button
                  key={item.label}
                  component={NavLink}
                  to={item.to}
                  className="sidebarItem"
                  activeClassName="active"
                  exact
                >
                  <ListItemIcon className="sidebarItemIcon">{item.icon}</ListItemIcon>
                  <ListItemText primary={item.label} className="sidebarItemText" />
                </ListItem>
              ))}
            </List>
          </div>
        </Drawer>
        <div className="content-container">
          <Routes>
            <Route path="/" element={<Introduction />} />
            <Route path="/team" element={<Team />} />
            <Route path="/about" element={<About />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
};

export default HomePage;
