import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import { Drawer, List, ListItem, ListItemIcon, ListItemText } from '@material-ui/core';
import { Home as HomeIcon, People as PeopleIcon, Info as InfoIcon } from '@material-ui/icons';
import './App.css'; // Import the CSS file for styling
import logo from './assets/logo.png'; // Import the logo image

import { makeStyles } from '@material-ui/core/styles';
import { Typography, Container, Box, AppBar, Toolbar, TextField, Button } from '@material-ui/core';

const HomePage = () => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  return (
    <Router>
      <div className="homepage">
        <header>
        <div className="header-container">
         <img src={logo} alt="Logo" className="logo" />
         <h1 className="title">Welcome to Arthmate</h1>
      </div>
        </header>
        <nav className="tabs">
          <Link to="/" className="tab-link" activeClassName="active">
            <HomeIcon className="tab-icon" />
            Introduction
          </Link>
          <Link to="/team" className="tab-link" activeClassName="active">
            <PeopleIcon className="tab-icon" />
            Team
          </Link>
          <Link to="/about" className="tab-link" activeClassName="active">
            <InfoIcon className="tab-icon" />
            What We Do
          </Link>
        </nav>
        <div className="content">
          <Routes>
            <Route path="/" element={<Introduction />} />
            <Route path="/team" element={<Team />} />
            <Route path="/about" element={<About />} />
          </Routes>
        </div>
        <Drawer anchor="left" open={isSidebarOpen} onClose={toggleSidebar}>
          <List>
            <ListItem button component={Link} to="/" onClick={toggleSidebar}>
              <ListItemIcon>
                <HomeIcon />
              </ListItemIcon>
              <ListItemText primary="Introduction" />
            </ListItem>
            <ListItem button component={Link} to="/team" onClick={toggleSidebar}>
              <ListItemIcon>
                <PeopleIcon />
              </ListItemIcon>
              <ListItemText primary="Team" />
            </ListItem>
            <ListItem button component={Link} to="/about" onClick={toggleSidebar}>
              <ListItemIcon>
                <InfoIcon />
              </ListItemIcon>
              <ListItemText primary="What We Do" />
            </ListItem>
          </List>
        </Drawer>
        <button className="toggle-sidebar-btn" onClick={toggleSidebar}>
          Toggle Sidebar
        </button>
      </div>
    </Router>
  );
};



const useStyless = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    backgroundImage: 'url("background-image.jpg")',
